/* eslint-disable react/jsx-pascal-case */
import { useEffect, useMemo, useState } from "react";
import { DateTime } from "luxon";
import { useRouter } from "next/router";
import Voltear_Numero from "./Voltear_Numero";
import { Contenido_contador } from "../../Styled/StyledComponent";


const Obtener_tiempo = (endDate) => {
    const now = DateTime.local();
    const { dias, horas, minutos, segundos } = endDate.diff(now, [
      "dias",
      "horas",
      "minutos",
      "segundos",
    ]);
    return {
      dias: Math.max(0, Math.trunc(dias)),
      horas: Math.max(0, Math.trunc(horas)),
      minutos: Math.max(0, Math.trunc(minutos)),
      segundos: Math.max(0, Math.trunc(segundos)),
    };
  };
  
  const useCountdown = (endDate) => {
    const initial = Obtener_tiempo(endDate);
    const [{ current, previous }, setCountdown] = useState({
      current: initial,
      previous: null,
    });
  
    useEffect(() => {
      const timer = window.setInterval(() => {
        setCountdown(({ current }) => {
          return {
            previous: current,
            current: Obtener_tiempo(endDate),
          };
        });
      }, 1000);
      return () => clearInterval(timer);
  
    }, []);
  
    return { previous, current };
  };
  
  export default function Countdown() {
    const router = useRouter();
    const defaultTime = useMemo(() => DateTime.local(2022, 4, 12, 0), []);
    const initialDate = router.query.date
      ? DateTime.fromISO(
          Array.isArray(router.query.date)
            ? router.query.date[0]
            : router.query.date
        )
      : null;
    const { current } = useCountdown(
      initialDate && initialDate.isValid ? initialDate : defaultTime
    );
  
    const INTERVAL = 1000;
  
    return (
      <Contenido_contador>
        {Object.keys(current).map((key) => {
          return (
            <div key={key}>
              <Voltear_Numero
                animationDuration={INTERVAL}
                number={current[key]}
              />
              <div className="mobile">{key.charAt(0).toUpperCase()}</div>
              <div className="desktop">
                {key.charAt(0).toUpperCase() + key.substr(1)}
              </div>
            </div>
          );
        })}
      </Contenido_contador>
    );
  }
